// idb-keyval - Simple IndexedDB wrapper
// This is a minimal mock for the actual library
;(() => {
  // Mock implementation - in real app, use actual idb-keyval library
  window.idbKeyval = {
    get: async (key) => localStorage.getItem(key),

    set: async (key, value) => {
      localStorage.setItem(key, JSON.stringify(value))
    },

    del: async (key) => {
      localStorage.removeItem(key)
    },

    clear: async () => {
      localStorage.clear()
    },

    keys: async () => Object.keys(localStorage),
  }
})()
